import React, { Component } from "react";
import axios from "axios";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fictionFountainList: [],
    };
  }

  componentDidMount() {
    axios.get('/csrf/')
      .then((response) => {
        this.csrftoken = response.data.csrfToken;
        this.refreshList();
      })
      .catch((error) => {
        console.log(error);
      });
  }

  refreshList = () => {
    axios
      .get("/api/fiction_fountains/")
      .then((res) => this.setState({ fictionFountainList: res.data }))
      .catch((err) => console.log(err));
  };

  renderItems = () => {
    this.refreshList();
    const newItems = this.state.fictionFountainList;

    return newItems.map((item) => (
      <div
        key={item.id}
        className="list-group-item d-flex flex-column align-items-start"
      >
        <span className={`todo-title mb-2`}>
          Genre: {item.genre}
        </span>
        <span className={`todo-title mb-2`}>
          People: {item.people}
        </span>
        <div>
          <p className="mb-1">Chapters:</p>
            {JSON.parse(item.chapters).map((chapter, index) => (
              <pre class="list-group-item d-flex flex-column align-items-start max-w-screen-md mx-auto whitespace-pre-wrap">
                {chapter}</pre>
            ))}
        </div>
      </div>
    ));    
  };

  render() {
    return (
      <main className="container">
        <h1 className="text-white text-uppercase text-center my-4">Todo app</h1>
        <div className="row">
          <div className="col-md-6 col-sm-10 mx-auto p-0">
            <div className="card p-3">
              <div className="mb-4">
                <button
                  className="btn btn-primary"
                  onClick={this.createItem}
                >
                  Generate
                </button>
              </div>
              <ul className="list-group list-group-flush border-top-0">
                {this.renderItems()}
              </ul>
            </div>
          </div>
        </div>
      </main>
    );
  }
}

export default App;
